 SZTBS_HEADER tbs_head;    /* ＴＢＳヘッダ情報    */
 SZ_KANSIGAI_MEMO_INF_S kgai_mem_inf; /* 監視対象外メモ情報   */
} SZKUT_KANSIGAI_MEMO_T;

typedef struct {
 char   new_kaisya_cd[4];  /* 新会社コード     */
 char   atm_dev_no[7];   /* ＡＴＭ装置番号    */
 char   kinyu_cd[4];   /* 申請行金融機関コード   */
 char   kisyu_cd[3];   /* 機種コード     */
 char   tenpo_no[6];   /* 店舗番号      */
 char   dev_sel_no[16];   /* 装置製造番号     */
} SZSEJI_ATMIF_T;

typedef struct {
 char   atmcd_auto_pro[1];  /* ＡＴＭ／ＣＤ自動処理   */
 char   atmcd_auto_pro_kind[1]; /* ＡＴＭ／ＣＤ自動処理結果  */
} SZSEJI_ATMCD_T;

typedef struct {
 char   kinko_id[8];   /* 金庫ＩＤ      */
 char   sousasya_kind[1];  /* 操作者種別     */
 char   sousasya_id[16];  /* 操作者ＩＤ     */
} SZSEJI_IDIF_T;

typedef struct {
 char   chadatu_kubun[1];  /* 着脱区分      */
 char   sihei_kubun[1];   /* 紙幣金庫区分     */
 char   kouka_kubun[1];   /* 硬貨金庫区分     */
} SZSEJI_CHADAI_T;

typedef struct {
 char   sousasya_kind[1];  /* 操作者種別     */
 char   sousasya_id[16];  /* 操作者ＩＤ     */
 char   pass_ward[8];   /* パスワード     */
 char   nyuten_mokuteki[1];  /* 入店目的      */
 char   nyutai_uchiwake[1];  /* 入退店内訳     */
 char   sagyou_naiyou[30];  /* 作業内容      */
} SZSEJI_NYUTAI_T;

typedef struct {
 char   kihon_baitai[1];  /* 基本媒体      */
 char   kihon_kamoku_cd[2];  /* 基本媒体上科目コード   */
 char   kihon_bank_cd[4];  /* 基本媒体上銀行コード   */
 char   kihon_siten_no[4];  /* 基本媒体上支店番号   */
 char   kihon_kouza_no[12];  /* 基本媒体上口座番号   */
 char   card_ms_data1[69];  /* カードＭＳデータ１   */
 char   card_ms_data2[37];  /* カードＭＳデータ２   */
 char   card_ms_data3[104];  /* カードＭＳデータ３   */
} SZSEJI_TORBAI_T;

typedef struct {
 char   atm_dev_no[7];   /* ＡＴＭ装置番号    */
 char   irai_no[10];   /* 依頼番号      */
 char   syutudo_kind[1];  /* 出動種別      */
 char   syutudo_riyuu[1];  /* 出動理由      */
 char   tochaku_yotei[12];  /* 到着予定時刻     */
 char   syutudo_umu[1];   /* 保守員出動有無コード   */
 char   hosyuin_tou_yotei[12]; /* 保守員到着予定時刻   */
} SZSEJI_TOCHAY_T;

typedef struct {
 char   uketuke_no[14];   /* 契約先受付番号    */
 char   syutu_yousei_no[4];     /* 出動要請管理番号             */
 char   atm_dev_no[7];          /* ＡＴＭ装置番号               */
 char   tochaku_yotei[12];      /* 到着予定時刻                 */
 char   hosyuin_simei[40];   /* 保守員出勤者氏名          */
} SZSEJI_HTOCHY_T;

typedef struct {
 char   irai_no[18];   /* 依頼番号      */
 char   atm_dev_no[7];          /* ＡＴＭ装置番号               */
